/**
 * @category alfred workflow
 * @version 1.0
 * @author Lucky
 */
require_once ('workflows.php');
$wf = new Workflows ();

//set variables
$base_url = "http://www.baidu.com/s?wd=";
$query = "{query}"; 

$data = $wf->request ( "http://suggestion.baidu.com/su?&zxmode=1&json=1&p=3&sid=&wd=" . urlencode ( $query ) ); // send request with query

preg_match ( '/(.*)"s":\[(.*)\],"/i', $data, $result ); // filter useless strings
$final_res = explode ( ',', $result [2] );

if (count ( $final_res ) > 1) { // whether results are valid

	foreach ( $final_res as $val ) {
		$keyword = str_replace ( '"', '', iconv ( 'GB2312', 'UTF-8', $val ) ); // convert string to UTF8
		$wd = urlencode ( $keyword );
		$link = $base_url . $wd;

		$wf->result ( time (), $link, $keyword, '', 'icon.png', 'yes' ); // create the final result
	}
} else {
	$link = $base_url . urlencode ( $query );
	$wf->result ( '1', $link, $query, '', 'icon.png', 'yes' );
}

echo $wf->toxml (); // create xml and output
